#!/bin/bashset -e  # exit on error--- 1. Add your ngrok auth token here#PYNGROK_KEY="2zuQ0T2wjvEGZHTFqH94NZ7iOmy_x9ZBEKxFEU6hxXaB4qaA"if [ -z "$PYNGROK_KEY" ]; thenecho "‚ö†Ô∏è Ngrok key not found"elsengrok config add-authtoken "$PYNGROK_KEY"fi--- 2. Start ngrok tunnel in backgroundngrok http 8501 > /tmp/ngrok.log 2>&1 &NGROK_PID=$!Give ngrok a moment to initializesleep 2--- 3. Fetch public URL from ngrok‚Äôs local APINGROK_URL=$(curl -s http://localhost:4040/api/tunnels | grep -o 'https://[^"]*' | head -n 1)if [ -z "$NGROK_URL" ]; thenecho "‚ö†Ô∏è Ngrok tunnel not available"elseecho "üîó Ngrok URL: $NGROK_URL"fi--- 4. Launch Streamlit appstreamlit run /content/app_crossdoc3_1.py   --server.port=8501   --server.headless=true   --server.address=0.0.0.0   --server.enableCORS=false &STREAMLIT_PID=$!echo "‚úÖ Enhanced Streamlit launched (PID: $STREAMLIT_PID)"echo "üìç Local: http://localhost:8501"